class Solution {
    public int integerBreak(int n) {
        int[] dp = new int[n+1];
        Arrays.fill(dp,-1);
        return helper(n,n,dp);
    }

    private int helper(int n,int q,int[] dp){
        if(n == 1)return 1;
        if(dp[n] != -1)return dp[n];

