class Solution {
public:

    // it is always optimal to split the number in form 2x+3y  if n>5
    // example - 8 as 3+3+2, 10 as 3+3+2+2
    // Proof intuition
    // Say number is of form 6+x, now product is 6*x but if we make it as 3+3+x its product is 9*x
    // also its better to split it into more threes than two as 3*(x-3) >= 2*(x-2) for x>=5
    // now we are left if three cases :
    // n%3 == 0 split it as n/3 3's
    // n%3 == 1 split it as (n/3 -1) 3's and two 2's
    // n%3 == 2 split it as n/3  3's and one two
    // can be done in O(logn) with binary exponentiation

    int binexp(int a,int b){
        if(b == 0) return 1;
        int res = binexp(a,b/2);
        if(b%2 == 0) return (res*res);
        return (a*res*res);
    }


    int integerBreak(int n) {
        // handle 2 to 5 separately
        if(n == 2) return 1;
        if(n == 3) return 2;
        if(n == 4) return 4;
        if(n == 5) return 6;

        if(n%3 == 0){
            return binexp(3,n/3);
        }
        if(n%3 == 1){
            return binexp(3,n/3 -1)*4;
        }
        if(n%3 == 2){
            return binexp(3,n/3)*2;
        }
        return 1;
    }
};``

