class Solution {
public:
    int integerBreak(int n) {

        int dp[n+1][n];

        for(int ctr = 0 ; ctr < n+1 ; ctr++)
            dp[ctr][0] = 1 ;

        for(int ctr = 0 ; ctr < n ; ctr ++)
            dp[0][ctr] = 1 ;

        for(int row = 1 ; row < n+1 ; row ++)
            for(int col = 1 ; col < n ; col ++)
            {

                if(row>=col)
                    dp[row][col] = max((col*dp[row-col][col]),dp[row][col-1]);
                else
                    dp[row][col] = dp[row][col-1];

            }


        return dp[n][n-1];

    }
};
