// Recursive Approach: TLE
var integerBreak = function (n) {
    let res = 1;

    function dfs(num) {
        if (num === 1) return 1;

        let res = num;
        for (let i = 1; i <= num; i++) {
            const tmpRes = i * dfs(num - i);
            res = Math.max(tmpRes, res);
        }

        return res;
    }
    for (let i = 1; i < n; i++) {
        const tmpRes = i * dfs(n - i);
        res = Math.max(tmpRes, res);
    }
    return res;
};

