            for(int j = 1; j <= i / 2; j++){
                max = Math.max(max, Math.max(j * (i - j), dp[j] * dp[i - j]));
                max = Math.max(max, Math.max(j * dp[i - j], dp[j] * (i - j)));
            }
            dp[i] = max;
        }
        return dp[n];
    }
}
