class Solution {
    int getMax(int n, vector<int> &dp){
        if(n <= 1) return 1;  //  base case 

        //  if the max mult exists for n return it..
        if(dp[n] != -1) 
            return dp[n];

        int ans = 1;
        //  now divide n from (1, n-1) to (n, 0) and recur 

        for(int i = 1; i <= n; i++){
            // mult is i*(next mult) 
            ans = max(ans, i*getMax(n-i, dp));
        }

        return dp[n] = ans;  // store the ans for n and return

    }
public:
    int integerBreak(int n) {
        if(n <= 3) return n-1; // as an edge case 3 = 2+1, 2 = 1+1

        //  The dp vector to store the max multiplication of numbers which sum upto i
        //  like dp[10] = 36 => (10 = 3+3+4) and 3*3*4 = 36
        vector<int> dp(n+1, -1); 

        return getMax(n, dp);  // call the function as a tabulation approach
    }
};
```

