class Solution {
public:
    int integerBreak(int n) {
        int i,j;
        vector<int> dp(5);  //constant space vector for those numbers which are not following the pattern (n=2 to 4)
        dp[2]=1;
        dp[3]=2;
        dp[4]=4;
        if(n<=4)
            return dp[n];
        int f=n/3;
        int ans=pow(3,f);
        if(n%3==0)  // if n is divisible by 3 we can obtain the number by adding 3 and answer by just multipying it
            return ans;
        if(n%3==1){   // if remainder is 1, ex: n=10 then answer is 3+3+4 i.e. ans=pow(3,f-1)*4
            ans=pow(3,f-1);  
            return ans*4;
        }
        ans=ans*(n%3); //basically its for n%3==2 case
        return ans;
    }
};
