class Solution {
public:
    int solve(int n , int k , int dp[]) {
        if(n < 0){
            return 0;
        }
        if(n == 0 && k >= 2){
            return 1;
        }
        if(dp[n] != -1){
            return dp[n];
        }
        int ans = 0;
        for(int i = 1 ; i <= n ; i++){
            int tempAns = i * solve(n - i , k + 1 , dp);
            ans = max(ans , tempAns);
        }
        return dp[n] = ans;
    }
    int integerBreak(int n) {
        int dp[n + 1];
        memset(dp , -1 , sizeof(dp));
        return solve(n , 0 , dp);
    }
};
