                        auto iter = sums_set.lower_bound(sum-k); //check the possible sum candidates;
                        if(iter != sums_set.end()) maxSum = max(maxSum, sum-*iter); //found one, check it;
                        sums_set.insert(sum);
                    }
                    ret = max(ret, maxSum);
                }
            }
            return ret;
        }
    };

