```C#
public class Solution {
    public int MaxSumSubmatrix(int[][] matrix, int k) {
        int rows = matrix.Length, cols = matrix[0].Length;

        int[][] sums = new int[rows + 1][];
        for (int i = 0; i <= rows; i++) sums[i] = new int[cols + 1];

        int ans = int.MinValue;
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                sums[i + 1][j + 1] = sums[i + 1][j] + sums[i][j + 1] - sums[i][j] + matrix[i][j];

                for (int rectangleHeight = 0; rectangleHeight <= i; rectangleHeight++) {
                    for (int rectangleWidth = 0; rectangleWidth <= j; rectangleWidth++) {
                        int rectangleSum = sums[i + 1][j + 1] - sums[i + 1][j - rectangleWidth] - sums[i - rectangleHeight][j + 1] + sums[i - rectangleHeight][j - rectangleWidth];
                        if (rectangleSum == k) return rectangleSum;
                        if (rectangleSum < k) ans = Math.Max(ans, rectangleSum);
                    }
                }
            }
        }

        return ans;
    }
}
