class Solution {
public:
    int process(vector<int> &v,int k){

        set<int> s;
        s.insert(0);
		// horizontal prefix sum to find actual sum of matrix 
        for(int i = 1;i<v.size();i++){
            v[i] += v[i-1];
        }
        int ans = INT_MIN;
		// since we want sum to be less than k so for some pre[i] a j must exist such that pre[i] - pre[j] <= k
		// this gives pre[j] >= pre[i] - k and can be easily found using lower_bound
        for(int i = 0;i<v.size();i++){
            int p = v[i];
            auto it = s.lower_bound(p-k);
            if(it != s.end()){
                ans = max(ans,p - *it);
            }
            s.insert(v[i]);
        }
        return ans;
    }
    int maxSumSubmatrix(vector<vector<int>>& matrix, int sum) {
        int n = matrix.size();
        int m = matrix[0].size();
		// vertical prefix sum for each matrix i.e. for each column
        for(int i = 0;i<m;i++){
            for(int j = 1;j<n;j++){
                matrix[j][i] += matrix[j-1][i];
            }
        }
        int ans = INT_MIN;
        for(int i = 0;i<n;i++){
            for(int j = i;j<n;j++){
			// fixing the height of matrix by setting starting row to i and ending row to j
                vector<int> temp;
				// generating vertical prefix sum for i to j 
                for(int k = 0;k<m;k++){
                    int val = matrix[j][k] - ((i-1>=0)?matrix[i-1][k]:0);
                    temp.push_back(val);
                }
                ans = max(ans,process(temp,sum));
            }
        }
        return ans;
    }
};
