                    sum = max(sum + arr[up], arr[up]);
                    g = max(g, sum);
                    if(g == k) 
                        return k;
                }
                if(g < k) 
                {
                    ans = max(ans, g);
                } 
                else 
                {
                    for(int up1 = 0; up1 < R; ++up1) 
