                        sum = 0;
                        for(int up2 = up1; up2 < R; ++up2) 
                        {
                            sum += arr[up2];
                            if(sum <= k) 
                                ans = max(ans, sum);
                        }

                        if(ans == k) return k;
                    }
                }
            }
        }
        return ans;
    }
};

