```
public class Solution {
    public int maxSumSubmatrix(int[][] matrix, int k) {
        int[] sumColumn = new int[matrix[0].length];
        int max = Integer.MIN_VALUE;
        for (int l = 0; l < matrix.length; l++) {
            // Reset sum column every time we move left index
            sumColumn = new int[matrix[0].length];

            // Iterate through combinations of left and right boundaries for columns
            for (int r = l; r < matrix.length; r++) { 

                // Sum all cell values across columns, starting from left moving towards right
                for (int i = 0; i < matrix[0].length; i++) {
                    sumColumn[i] += matrix[r][i];
                }

                // Calculate the local maximum using kadane's and take the max between previous max and new value
                max = Math.max(max, kadanes(sumColumn, k));                 
                if (max == k) { return k; }
            }
        }
        return max;
    }

    // Like normal kadane's except we use a TreeSet since we don't want to exceed value k
    private int kadanes(int[] arr, int k) {
        int max = Integer.MIN_VALUE;
        int curr = 0;

        // Create tree set to find local maximum with respect to k
        TreeSet<Integer> set = new TreeSet<>();

        // Populate with 0 for the case where curr - k is smaller than 0
        set.add(0);
        for (int i = 0; i < arr.length; i++) {
            curr += arr[i];

            // Use the previous sums to determine the smallest previous sum. that can be subtracted from the 
            // current sum to stay under the value k, by taking the ceiling value for curr - k
            Integer ceil = set.ceiling(curr - k);
            if(ceil != null) {
                max = Math.max(max, curr - ceil);
            }
            set.add(curr);
        }
        return max;
    }
}
