                        run_sum+=sum;
                        auto it = st.lower_bound(run_sum-k);
                        if(it!=end(st)){
                            res = max(res,run_sum-*it);
                            if(res==k){
                                return res; // doing this for handling TLE
                            }
                        }
                        st.insert(run_sum);
					}
				}
