```
class Solution {
public:
  int maxSumSubmatrix(vector<vector<int>>& matrix, int k) {

    int n=matrix.size(), m = matrix[0].size();
    vector<vector<int>> cum(n, vector<int> (m, 0));
    //cum[i][j] gives area of rectangle from (0,0) to (i,j)

    //Filling the cum matrix
    cum[0][0] = matrix[0][0];

    //filling 0th row
    for(int row=1; row<n; row++){
      cum[row][0] = cum[row-1][0] + matrix[row][0];
    }
    //filling 0th col
    for(int col=1; col<m; col++){
      cum[0][col] = cum[0][col-1] + matrix[0][col];
    }     

    for(int row=1; row<n; row++){
      for(int col=1; col<m; col++){
        cum[row][col] = matrix[row][col] + (cum[row][col-1] + cum[row-1][col] - cum[row-1][col-1]);
      }
    }
    //checking the values in cum matrix
    // for(int row=0; row<n; row++){
    //   for(int col=0; col<m; col++){
    //     cout<<cum[row][col]<<" ";
    //   }cout<<endl;
    // }

    int max_area = INT_MIN, cur_area;

    for(int st_row=0; st_row<n; st_row++){
      for(int st_col=0; st_col<m; st_col++){

        for(int en_row=st_row; en_row<n; en_row++){
          for(int en_col=st_col; en_col<m; en_col++){

            cur_area = INT_MIN;

            if(en_row==st_row && en_col==st_col){
              cur_area = matrix[en_row][en_col]; //single ele max case 
            }
            else{
              if(st_row==0 && st_col==0){
                cur_area = cum[en_row][en_col];
              }
              else if(st_row == 0){
                cur_area =  cum[en_row][en_col] - cum[en_row][st_col - 1];
              }
              else if(st_col == 0){
                cur_area = cum[en_row][en_col] - cum[st_row - 1][en_col];
              }
              else{
                cur_area = cum[en_row][en_col] - cum[en_row][st_col - 1] - cum[st_row - 1][en_col] + cum[st_row - 1][st_col - 1];
              }
            }

            // cout<<cur_area<<" ";
            if(cur_area <= k){
              max_area = max(max_area, cur_area);
            }
          }
        }
      }
    }
    return max_area;
  }
};
