class Solution {
public:
    ll dp[105][105];

    ll get(ll x1,ll y1,ll x2,ll y2){
        return dp[x2+1][y2+1] - dp[x1][y2+1] - dp[x2+1][y1] + dp[x1][y1];
    }
    int maxSumSubmatrix(vector<vector<int>>& arr, int ch) {
        ll n = arr.size(),m = arr[0].size();
        for(ll i = 0;i <= n;i++){
            for(ll j = 0;j <= m;j++){
                dp[i][j] = 0;
            }
