class Solution {
public:
    int maxSumSubmatrix(vector<vector<int>>& mat, int k) {
        int m = mat.size(), n = mat[0].size(), res = INT32_MIN;
        for(int i = 0; i < m; i++) {
            for(int j = 1; j < n; j++) {
                mat[i][j] += mat[i][j-1];
            }
        }

        for(int i = 0; i < n; i++) {
            for(int j = i; j < n; j++) {
                vector<int> pref(m);
                for(int k = 0; k < m; k++) pref[k] = mat[k][j]-(i > 0? mat[k][i-1]:0);
                for(int k = 1; k < m; k++) pref[k] += pref[k-1];
                set<int> st;

                for(int l = -1; l < m; l++) {
                    int curr = l >= 0? pref[l]:0;
                    auto it = st.lower_bound(curr-k);
                    if(it != st.end()) {
                        res = max(res,curr-(*it));
                    }
                    st.insert(curr);
                }
            }
        }

        return res;
    }
};
