class Solution {
public:
    int maxSumSubmatrix(vector<vector<int>>& matrix, int K) {

        int num_rows = matrix.size(), num_cols = matrix[0].size();
        int ans = INT_MIN;

        for(int l=0;l<num_cols;l++){
            vector<int>row_sum(num_rows,0);
            for(int r=l;r<num_cols;r++){
                for(int k=0;k<num_rows;k++)
                    row_sum[k] += matrix[k][r];
                int sum = 0;
                set<int>s;
                s.insert(0);
                if(num_rows==1){
                    if(row_sum[0]<=K)
                        ans = max(ans,row_sum[0]);
                    continue;
                }
                for(int k=0;k<num_rows;k++){
                      sum+=row_sum[k];
                      auto it = (s.lower_bound((sum-K)));
                      if(it!=s.end()){
                          int x = *it;
                          ans = max(ans, sum-x);
                      }
                      s.insert(sum);
                }
            }
        }
        return ans;
    }
};
