class Solution:
    def maxSumSubmatrix(self, matrix: List[List[int]], k: int) -> int:
        r,c = len(matrix), len(matrix[0])
        ans = -math.inf
        for up in range(r):
            curr = [0]*c
            for down in range(up,r):
                curr = [curr[i]+matrix[down][i] for i in range(c)]
                seen = [0]
                sm = 0
                for i in range(c):
                    sm += curr[i]
                    ind = bisect.bisect_left(seen,sm-k)
                    if ind < len(seen) and sm-seen[ind] > ans:
                        ans = sm-seen[ind]
                    bisect.insort_right(seen,sm)
        return ans
