    boolean search(String cur, String end, Map<String, List<String>> map) {
        if (cur.equals(end)) return true;
        List<String> states;
        if (map.containsKey(cur)) states = map.get(cur);
        else {
            states = getAllStates(cur);
            map.put(cur, states);
        }
        for (String s : states) {
            if (search(s, end, map)) return true;
        }
        return false;
    }

    List<String> getAllStates(String s) {
        List<String> states = new ArrayList<>();
        for (int i = 0;i < s.length() - 1;i++) {
            String ss = s.substring(i, i + 2);
            if (ss.equals("XL")) {
                String newS = s.substring(0, i) + "LX" + s.substring(i + 2, s.length());
                states.add(newS);
            }

            if (ss.equals("RX")) {
                String newS = s.substring(0, i) + "XR" + s.substring(i + 2, s.length());
                states.add(newS);
            }
        }
        return states;
