            while (si < start.size() && start[si]=='X') si++;
            while (ei < end.size() && end[ei]=='X') ei++;

            // start = "XXL" --> start[si] = 'L'
            // end   = "RXX" -->   end[ei] = 'R'
            if (start[si] != end[ei])
                return false;

            // start = "XXR" --> start[si] = 'R'
            // end   = "RXX" -->   end[ei] = 'R'   --> XXR cannot be converted to RXX, i.e., si cannot be greater than ei
            if (start[si]=='R' && si>ei)
                return false;

            // start = "LXX" --> start[si] = 'L'
            // end   = "XXL" -->   end[ei] = 'L'   --> LXX cannot be converted to XXL, i.e., si cannot be less than ei
            if (start[si]=='L' && si<ei)
                return false;

            si++;
            ei++;
        }
        return true;
    }
};
