class Solution {
    public boolean canTransform(String start, String end) {
        Stack<Integer> L = new Stack(), R = new Stack();
        for (int i = 0; i < start.length(); i++) {
            if (start.charAt(i) == 'R')
                R.add(i);
            if (end.charAt(i) == 'L')
                L.add(i);
            if (start.charAt(i) == 'L') {
                if (L.isEmpty() || !R.isEmpty() && L.peek() >= R.peek())
                    return false;
                else 
                    L.pop();
            } 
            if (end.charAt(i) == 'R') {
                if (R.isEmpty() || !L.isEmpty() && R.peek() >= L.peek())
                    return false;
                else
                    R.pop();
            } 
        }
        if (!L.isEmpty() || !R.isEmpty())
            return false;
        return true;
    }
}
