// Replace all 'X' in both strings and see if they are SAME then return true
// Whenever you see 'X' just update the idx

// TC : O(N) -> N = start.length
// SC : O(1)
```
class Solution {
    public boolean canTransform(String start, String end) {
        int startIdx = 0;
        int endIdx = 0;

        while(startIdx < start.length() || endIdx < end.length()) {
            while(startIdx < start.length() && start.charAt(startIdx) == 'X') {
                startIdx++;
            }

            while(endIdx < end.length() && end.charAt(endIdx) == 'X') {
                endIdx++;
            }

            if(startIdx >= start.length() || endIdx >= end.length()) break; // come out of while loop

            if(start.charAt(startIdx) != end.charAt(endIdx)) return false;

            // if the character is 'L', it can only be moved to the LEFT. startIdx should be greater or equal to endIdx. 
            if(start.charAt(startIdx) == 'L' && endIdx > startIdx) {
                return false;
            }

            // if the character is 'R', it can only be moved to the RIGHT. endIdx should be greater or equal to startIdx.
            if(start.charAt(startIdx) == 'R' && startIdx > endIdx) {
                return false;
            }

            startIdx++;
            endIdx++;
        }

        return startIdx == endIdx; // if while loop exited because of the string (start or end) reach end but not other, return false
    }
}
```

