/**
* @param {string} start
* @param {string} end
* @return {boolean}
*/
var canTransform = function (start, end) {
    const sWithoutX = start.replace(/X/g, '');
    const eWithoutX = end.replace(/X/g, '');

    // start and end text without x's should equal
    if (sWithoutX !== eWithoutX) return false;

    const sCount = { l: 0, r: 0 };
    const eCount = { l: 0, r: 0 };

    // iterate through original start string
    for (let i = 0; i < start.length; i++) {

        // track occurances of L's and R's
        if (start[i] === 'L') {
            sCount.l++;
        } else if (start[i] === 'R') {
            sCount.r++;
        }

        // also track L/R occurances on end string
        if (end[i] === 'L') {
            eCount.l++;
        } else if (end[i] === 'R') {
            eCount.r++;
        }

        // if at any point during iteration, start has more l's than end
        // or if start has less r's than end - path impossible
        if (sCount.l > eCount.l || sCount.r < eCount.r) return false;
    }

    return true;

};
```

