public boolean swapAdjacentInLRString(String s, String e) {
	if(s.length() != e.length()) return false;
	int i = 0, j = 0;
	char[] st = s.toCharArray();
	char[] ed = e.toCharArray();
	while(i < st.length || j < ed.length) {
		// only need to work with 'L' and 'R' so ignore 'X'
		while(i < st.length && st[i] == 'X'){
			++i;
		}
		while(j < ed.length && ed[j] == 'X') {
			++j;
		}
		// both at end
		if(i == st.length && j == ed.length) {
			return true;
		}
		// one of them at the end
		if(i == st.length || j == ed.length) {
			return false;
		}
		// character not similar
		if(st[i] != ed[j]) return false;
		// if char is R we can go right only when i pointer doesn't exceed j pointer
		if(st[i] == 'R' && i > j) return false;
		// if char is L we can go left only when i pointer exceed j pointer
		if(st[i] == 'L' && i < j) return false;
		i++;
		j++;
	}
	return true;
}
