var canTransform = function(start, end) {
	let L = R = 0;

	for (let index = 0; index < start.length; index++) {
		start[index] === 'R' && R++;
		end[index] === 'L' && L++;
		if (R > 0 && L > 0) return false;

		start[index] === 'L' && L--;
		end[index] === 'R' && R--;
		if (L < 0 || R < 0) return false;
	}
	return L === 0 && R === 0;
};

