class Solution {
    public boolean canTransform(String start, String end) {
        List<Pair<Character, Integer>> startRandL = new ArrayList<>();
        List<Pair<Character, Integer>> endRandL = new ArrayList<>();

        for(int i = 0; i < start.length(); i++) {
            if(start.charAt(i) != 'X') {
                startRandL.add(new Pair(start.charAt(i), i));
            }
            if(end.charAt(i) != 'X') {
                endRandL.add(new Pair(end.charAt(i), i));
            }
        }

        if(startRandL.size() != endRandL.size()) {
            return false;
        }

        for(int i = 0; i < startRandL.size(); i++) {
            Pair<Character, Integer> startPair = startRandL.get(i);
            Pair<Character, Integer> endPair = endRandL.get(i);
            if(startPair.getKey() == 'L' && endPair.getKey() == 'L') {
                if(startPair.getValue() < endPair.getValue()) {
                    return false;
                }
            }
            else if(startPair.getKey() == 'R' && endPair.getKey() == 'R') {
                if(startPair.getValue() > endPair.getValue()) {
                    return false;
