class Solution {
    public boolean canTransform(String start, String end) {

        String removedStart = start.replaceAll("X","");
        String removedEnd = end.replaceAll("X","");

        //L's and R's can't cross, so after we've pruned the X's, the strings should match.
        //If they don't, the starting and ending strings cannot be made to match.
        if(!removedStart.equals(removedEnd))
            return false;

        //Can the Ls move only left?
        if(!checkLOrROnlyMoveLeftOrRight(start, end, "L"))
            return false;

        //Can the Rs move only right?
        if(!checkLOrROnlyMoveLeftOrRight(start, end, "R"))
            return false;

        return true;

    }

    /**
     * This function checks to be sure that L's in the start string only
     * need to move left to match the end string.
     */
    private boolean checkLOrROnlyMoveLeftOrRight(String start, String end, String lor)
    {
        //Find the first L/R in the end string:
        int endIndex = end.indexOf(lor);
        int startIndex=0;
        while(endIndex>-1) //While there are more L's/R's...
        {
            //Find the next index of L/R in the starting index.
            startIndex = start.indexOf(lor, startIndex);

            switch(lor){
                case "L":
                    //If the starting index of L is less than the ending index of L, then return false
                    //because L's can only move left.
                    if(startIndex<endIndex)
                        return false;
                        break;
                case "R":
                    //If the starting index of R is greater than the ending index of R, then return false
                    //because R's can only move right.
                    if(startIndex>endIndex)
                        return false;
                        break;
                default:
                    //In production, throw an error. Here, not so much.
                    break;
            }

            //The next time we search for an L/R in the start string, search after the L/R
            //that was just found.
            startIndex++;
            //Find the next L/R in the end string.
            endIndex = end.indexOf(lor,endIndex+1);
        }

        return true;
    }

}
