class Solution:
    def canTransform(self, start: str, end: str) -> bool:
        if start.replace('X', '') != end.replace('X', ''):
            return False
        else:
            """
            We construct the two lists of indices.
            """
            index_L = []
            index_R = []
            for i, ch in enumerate(start):
                if ch == 'L':
                    index_L.append(i)
                elif ch == 'R':
                    index_R.append(i)

            """
            We check the indicies of the elements.
            """ 
            for i, ch in enumerate(end):
                if ch == 'L':
                    if index_L[0] >= i:
                        index_L.pop(0)
                    else:
                        return False
                elif ch == 'R':
                    if index_R[0] <= i:
                        index_R.pop(0)
                    else:
                        return False
            else:
                return True

