    bool canTransform(string start, string end) {
        int n=start.size();
        int px=0,pr=0;
        for(int i=0,j=0; i<n && j<n; j++)
        {
            if(start[i]==end[j])
            {
                i++;
            }
            else if(end[j]=='R') 
            {
                return false;
            }
            else
            {
                char c2=end[j];
                char c1=(c2=='L')?'X':'R';

                int k=max(i,(c2=='L')?px:pr);
                while(k<n && start[k]==c1) k++;
                if(k==i || k==n || start[k]!=c2) return false;

                start[k]=c1;                
                start[i]=c2;
                if(c2=='L') px=k;
                else pr=k;
                i++;
            }
        }

        return true;
    }
};
