    bool canTransform(string start, string end) {
        if(start.size() != end.size())
            return false;

        //Notice that index of L in start will be greater than index of L in end.
        //Index of R in start will be less than index of R in end.
        //Record all L and R positions in both strings and verify this condition.
        //Stack for start and end. {L or R, its index position corresponding string}
        stack<pair<char, int>> stStart, stEnd;

        //Take only indices of L and R in start and end string
        for(int i = 0; i < start.size(); i++)
        {
            if(start[i] != 'X')
                stStart.push({start[i], i});
            if(end[i] != 'X')
                stEnd.push({end[i], i});
        }

        //If they don't have same number of L's and R's, return false
        if(stStart.size() != stEnd.size())
            return false;

        //Loop through all the elements in both stacks
        while(!stStart.empty())
        {
            //take characters from start and end stacks and their corresponding indexes from the pair.
            char first = stStart.top().first, second = stEnd.top().first;
            int i = stStart.top().second, j = stEnd.top().second;

            //pop seen elements.
            stStart.pop();
            stEnd.pop();

            //If characters are not the same in the sequence, then return false.
            if(first != second)
                return false;
            //If we have R on both strings, then R in start must be less than R in end. If not, return false.
            else if(first == 'R' && i > j)
                return false;
            //If we have L on both strings, then L in start must be more than L in end. If not, return false.
            else if(first == 'L' && i < j)
                return false;
        }
        return true;
    }
