impl Solution {
    pub fn can_transform(start: String, end: String) -> bool {
        let start = start.chars().enumerate().filter(|&(i,c)| c!='X').collect::<Vec<(_,_)>>();
        let end   =   end.chars().enumerate().filter(|&(i,c)| c!='X').collect::<Vec<(_,_)>>();
        let len_s = start.len();
        let len_e = end.len();

        if len_s != len_e {
            return false;
        }

        for i in 0..len_s {
            let (pos_s,c_s) =start[i]; 
            let (pos_e,c_e) = end[i]; 
            if c_e != c_s {
                return false;
            }
            if c_s == 'L' && c_e == 'L' {
                if pos_s < pos_e {
                    return false;
                }
            }
            if c_s == 'R' && c_e == 'R' {
                if pos_s > pos_e {
                    return false;
                }
            }
        }

        true

    }
}
