class Solution {
public:

    bool canTransform(string start, string end) {
        //check if all relative order of R and L are the same
        int n = start.size();
        int i = 0, j = 0;
        while (i < n || j < n) {
            while (i < n && start[i] == 'X') i++;
            while (j < n && end[j] == 'X') j++;

            //here  i, j piont to 'X' or 'R'
            //(1-1) If there are more 'X' or 'R' in one string than the other return false 
            if (i == n && j != n) return false;
            if (i != n && j == n) return false;
            //(1-2) 'X' and 'R' in both strings must have the same relative orders

            if (start[i] != end[j]) return false;
            //(2-1) If the current char is 'L', then its position in start must to the right of that in end, b/c 'L' can only move to left.
            //If this is not the case, return false;
            if (start[i] == 'L' && j > i) return false;
