            char sc=start.charAt(s);
            char tc=end.charAt(e);
            if(sc!=tc)
                return false;
            if ((tc=='L' && s<e) || (tc=='R' && s>e))
                return false;
            s++;
            e++;
        }
        return true;
    }
}
