def myAtoi(self, str):
    str = str.strip()
    if str == "":
        return 0
    if str[0] != "+" and str[0] != "-" and not str[0].isdigit():
        return 0
    else:
        if str[0] in ["+", "-"]: # first letter is "+" or "+"
