class Solution {
public:
    int myAtoi(string s) {
        bool flag=0,tag=0,start=0,zero=0;
        std::string::iterator itr=s.begin();
        std::vector<int>::iterator it;
        vector<int>res;
        long p=0;
        while(itr!=s.end())
        {
            //cout<<*itr<<endl;
            if(*itr=='+')
            {
                if(flag==1||zero==1||start==1||tag==1)
                    break;
                tag=1;
            }
            if(*itr=='0')
            {
                if(start==1)
                    res.push_back(0);
                zero=1;
            }
            if(*itr=='-')
            {
                if(tag==1||zero==1||start==1||flag==1)
                    break;
                flag=1;
            }
            if(*itr==' ')
            {
                if(start==1||tag==1||zero==1||flag==1)
                    break;
            }
            if((int)*itr<58&&(int)*itr>48)
            {
                start=1;
                res.push_back((int)*itr-48);
            }
            if((int)*itr>57||(int)*itr<48)
            {
                if(start==1)
                    break;
                if(*itr!='-'&&*itr!=' '&&*itr!='+'&&*itr!='0')
                    return 0;
            }
            itr++;
        }
        it=res.begin();
        cout<<res.size()<<endl;
        if(res.size()>10)
        {
            if(flag==1)
                return INT_MIN;
            else
                return INT_MAX;
        }
        for(int i=res.size()-1;i>=0;i--,it++)
        {
            if(flag==1&&tag==0)
                p-=(*it)*pow(10,i);
            else
                p+=(*it)*pow(10,i);
            if(p>=INT_MAX)
                return INT_MAX;
            if(p<=INT_MIN)
                return INT_MIN;
        }
        return p;
    }
};
