        }
        if(sum<Integer.MIN_VALUE)
        {
            sum=Integer.MIN_VALUE;
        }
        if(sum>Integer.MAX_VALUE)
        {
            sum=Integer.MAX_VALUE;
        }
        return (int)sum;
    }
