      var numberStr = ""
      var number = 0

      for character in str {

        if character == " " && numberStr.isEmpty {
            continue
        } else if Int(String(character)) != nil {
          numberStr.append(character)
        } else if (character == "+" || character == "-") && numberStr.isEmpty {
          numberStr.append(character)
        } else {
          break
        }

