      if let num = Int(numberStr) {
        if num > Int32.max {
          number = Int(Int32.max)
        } else if num < Int32.min {
          number = Int(Int32.min)
        } else {
          number = num
        }
      } else if numberStr.count > 1 {
        if numberStr.first == "-" {
          number = Int(Int32.min)
        } else {
          number = Int(Int32.max)
        }
      }
      return number
    }
