class Solution {
public:
    bool isDigit(char c){
        return (c>='0' && c<='9');
    }

    int myAtoi(string s) {
        int i=0; int n=s.length();
        bool neg=false;
        // step1 => Read in and ignore any leading whitespace.
        while(s[i]==' '){
            i++;
        }
        // step2 => Check if the next character (if not already at the end of the string) is '-' or '+'. 
        // Read this character in if it is either. This determines if the final result is negative or 
        // positive respectively. Assume the result is positive if neither is present.
        if(s[i]=='-'){
            neg=true;
            i++;
        }
        else if(s[i]=='+'){
            i++;
        }
        // step3 => Read in next the characters until the next non-digit charcter or the end of the input is reached. 
        // The rest of the string is ignored.
        int num=0;
        while(isDigit(s[i]) && i<n){
            if(!neg){
                if(num>INT_MAX/10 || (num==INT_MAX/10 && s[i]-'0'>=7)){
                    return INT_MAX;
                }
            }else{
                if(num>INT_MAX/10 || (num==INT_MAX/10 && s[i]-'0'>=8)){
                    return INT_MIN;
                }
            }
            num=num*10+(s[i]-'0');
            i++;
        }
        if(neg){
            num=-1*num;
        }
        return num;
    }
};
