pub fn atoi(str: String) -> i32 {
    use std::i32;

    let mut s: i32 = 0;
    let mut negated = false;
    let mut started = false;

    for c in str.chars() {
        match c {
            ' ' if !started => continue,
            '+' if !started => started = true,
            '-' if !started & !negated => {
                started = true;
                negated = true;
                s = -s
            }
            '0'..='9' => {
                started = true;

                s = match s.checked_mul(10).and_then(|s| s.checked_add(c as i32 - '0' as i32)) {
                    Some(v) => v,
                    None => return if negated { i32::MIN } else { i32::MAX },
                };
            }
            _ => break,
        }
    }

    if negated {
        -s
    } else {
        s
    }
}
