public int myAtoi(String str) {
	long result =0;
	Boolean isNegative = null, hasNumberStarted = false;
	for(char ch: str.toCharArray()) {
		if(ch>='0' && ch <='9') {
			hasNumberStarted = true;
			result = result * 10 + ch - '0';
			if(result > Integer.MAX_VALUE) 
				return isNegative !=null && isNegative?Integer.MIN_VALUE:Integer.MAX_VALUE;
		} else if(!hasNumberStarted) {
			if((ch == '-' || ch == '+') && isNegative == null) {
				hasNumberStarted = true;
				isNegative = ch == '-'?true:false;
			} else if(ch != ' ') {
				break;
			}
		} else break;
	}
	return (int)(isNegative !=null && isNegative?-result:result);
}
