class Solution {
private:
    bool isOverflow(char op, int64_t num){
        if (op == '+'){
            return num > INT32_MAX;
        } else {
            return num -1 > INT32_MAX;
        }
    }

    int clamp(char op){
        if (op == '+'){
            return INT_MAX;
        }
        return INT_MIN;
    }

public:
    int myAtoi(string s) {
        int64_t num = 0;

        // remove heading whitespace
        s.erase(0, s.find_first_not_of(" "));

        // symbol + or -
        int startIdx = 0;
        char op;
        if(isdigit(s[0])){
            op = '+';
        }
        if (s[0] == '+' || s[0] == '-') {
            startIdx = 1;
            op = s[0];
        }


        // read untilthe end or any non-numeric char 
        for(int i = startIdx; i < s.length(); i++){
            if (!isdigit(s[i])){
                break;
            }
            num = num*10 + s[i] - '0';

            if(isOverflow(op, num)){
                return clamp(op);
            }
        }

        if (op == '-') num = num * (-1);
        return num;
    }
};

