public int MyAtoi(string s) {
        s = s.Trim();
        if(s.Length==0 || (s.Length==1 && !Char.IsDigit(s[0])))
            return 0;

        int i = s[0]=='-' || s[0]=='+'
                ? 1 // index skips sign if present
                : 0;
        bool isNeg = s[0]=='-'; // determine sign
        long curr = 0;
        while(i<s.Length) {
            if(!Char.IsDigit(s[i])) break; // rest of the string is ignored
            curr *= 10;
            curr = isNeg ? curr - (s[i++]-'0') : curr + (s[i++]-'0');
            if(curr>int.MaxValue || curr<int.MinValue) // stop early optimization
                return curr < 0 ? int.MinValue : int.MaxValue;
        }
        return (int)curr;
    }
