class Solution {
public:
    int uniqueMorseRepresentations(vector<string>& words) {
        // store morse codes for each letter 
        vector<string> hash = {".-","-...","-.-.","-..",".","..-.","--.","....","..",
                                ".---","-.-",".-..","--","-.","---",".--.","--.-",".-.",
                                "...","-","..-","...-",".--","-..-","-.--","--.."};

        set<string> s ;
        for (int i = 0 ; i < words.size() ; i++)
        {
            string temp = "" ;
            for (int j = 0 ; j < words[i].length() ; j++)
            {
                // create string of morse of each word 
                temp += hash[words[i][j] - 'a'] ;
            }
            // store into a set, set stores only unique elements 
            s.insert(temp) ;
        }
        // size of set gives unique morse codes 
        return s.size() ; 
    }
};
