int uniqueMorseRepresentations(vector<string>& words) {
	set<string> s;

	// we map with vector as 0 based indexing for chars, {'a', ".-"}, {'b', "-..."}.....
	vector<string> vec ={".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."};
	string st;

	// then we traverse the string and for each characters from the word, 
	// we get its morse code and add it to the set
	for(int i=0; i<words.size(); i++){
		st = "";
		for(int j=0; j<words[i].length(); j++)
			st+=vec[words[i][j]-'a'];

		// then we add this string to the set, and continue for all the words
		s.insert(st);
	}
	return s.size();
}
