var uniqueMorseRepresentations = function(words) {
    // set morse array (26 elements like english letters)
    morse = [".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."]    
    // set array to store result of words transformation
    arr = []
    // enumerate all word in words array
    for (word of words) {        
        let temp = []
        // transform letter to morse
        for(let i = 0; i < word.length; i++)
            temp.push(morse[word[i].charCodeAt(0) - 97])
        // push result in arr
        arr.push(temp.join(''))
    }   
    // return unique values number in arr
    return [...new Set(arr)].length
};
