class Solution {
public:
    int uniqueMorseRepresentations(vector<string>& words) {
        unordered_set<unsigned long> results;
        vector<pair<int, unsigned>> alphabet = {{2, 0b01}, {4 ,0b1000}, {4 ,0b1010}, {3 ,0b100}, {1 ,0b0}, {4 ,0b0010}, {3 ,0b110}, {4 ,0b0000}, {2 ,0b00}, {4 ,0b0111}, {3 ,0b101}, {4 ,0b0100}, {2 ,0b11}, {2 ,0b10}, {3 ,0b111}, {4 ,0b0110}, {4 ,0b1101}, {3 ,0b010}, {3 ,0b000}, {1 ,0b1}, {3 ,0b001}, {4 ,0b0001}, {3 ,0b011}, {4 ,0b1001}, {4 ,0b1011}, {4 ,0b1100}};
        for(size_t i = 0; i < words.size(); ++i)
        {
            unsigned long result = 0;
            for(size_t j = 0; j < words[i].size(); ++j)
            {
                auto cur = alphabet[words[i][j] - 'a'];
                result = result << cur.first | cur.second;
            }
            results.insert(result);
        }        
        return results.size();

    }
};

