def unique_morse_representations(words)

    letter = ['a','b','c','d','e','f','g','h','i','j','k','l','m','n','o','p','q','r','s','t','u','v','w','x','y','z']
    morseLetter = [".-","-...","-.-.","-..",".","..-.","--.","....","..",".---","-.-",".-..","--","-.","---",".--.","--.-",".-.","...","-","..-","...-",".--","-..-","-.--","--.."]
    translation = {}
    morseWords = []

    # map each letter to its morse code equivalent
    letter.each_with_index {|k,v|translation[k] = morseLetter[v]}

    # iterate through words and get morse code translation
    words.each{ |word| morseWords << word.split("").map{ |char| translation[char] }.join("") }

    # remove duplicates and return length
    return morseWords.uniq.length


end
